    &lt;link
      href=&quot;https://fonts.googleapis.com/css2?family=JetBrains+Mono:wght@400;500;600;700&amp;display=swap&quot;
      rel=&quot;stylesheet&quot;
    /&gt;
    &lt;style&gt;
      /* Override defaults for terminal-like appearance */
      .terminal-output pre {
        font-family: &quot;JetBrains Mono&quot;, monospace;
        padding: 0;
        margin: 0;
        overflow-x: visible;
        border: none;
        background: transparent;
        color: var(--text-color);
        line-height: 1.2;
        font-size: 0.95rem;
      }

      /* Fix spacing in terminal output */
      .terminal-output {
        padding-left: 0;
        margin-top: 0.25rem;
        margin-bottom: 0.5rem;
      }

      /* Tighter paragraphs for terminal feel */
      .terminal-output p {
        margin: 0;
        line-height: 1.2;
      }

      /* Add small space between paragraphs but keep compact */
      .terminal-output p:not(:last-child) {
        margin-bottom: 0.1rem;
      }

      /* Contact options with no large gaps */
